def welcome():
    return "Welcome To Decentralised Resource Management"

def addResourcedata():

  date1 = st.date_input("Please select the date")
  time1 = st.time_input("Enter your slot timings")
  time = str(time1)
  Aname = st.text_input("Enter the Associates Name")
  aid = st.number_input("Enter the associates Unique Identification id ",step=1, min_value=410897982627)
  date = str(date1)
  aid1 = str(aid)
  Name_date = date+Aname+aid1
  APhoneNumber = st.number_input("Enter the Contact Number of Associate", step=1, min_value=1234567892)
  Mname = st.text_input("Enter the name of Resource Mineral for eg: Coal,Crude Oil etc")
  loo = st.text_input("Enter the place of origin of the mineral")
  qty = st.text_input("Enter the Quantity of mineral brought")
  qts = st.selectbox("Quality Status: ",
                     ['Passed', 'Not passed', 'Need Some attention'])
  Quality = st.text_input("Enter the Quality points out of 10")
  comment = st.text_input("Any other Important Comments")


  if st.button("Save Data"):
    a = pytezos.using(shell = 'https://granadanet.smartpy.io', key='edskRn4MXH7DD9M9QiAKhKRNLCp4LU11iqEcNp6vSqNWB8qksLZckKgQZozdVuX4wum61fNwaesWjgtydm8f3h6NYggFAtPx4f')
    contract = a.contract('KT1RArtwEHEBPA7c8ggfg2nN2kb6dvD6vnDK')

    contract.addResourcedata(time=time,aid=aid,date=date,Name_date=Name_date,loo=loo,qts=qts,Aname=Aname,APhoneNumber=APhoneNumber,Mname=Mname,qty=qty,Quality=Quality,comment=comment).with_amount(0).as_transaction().fill().sign().inject()   

def ViewResourceData():
  date1 = st.date_input("Enter the Date for which you want to see the data")
  Aname = st.text_input("Enter the Associates Name")
  aid1 = st.number_input("Enter the associates Unique Identification id ",step=1, min_value=410897982627)
  date =str(date1)
  aid =str(aid1)
  Name_date = date+Aname+aid


  if st.button("View Records"):
    usds = pytezos.using(shell = 'https://granadanet.smartpy.io').contract('KT1RArtwEHEBPA7c8ggfg2nN2kb6dvD6vnDK')
    st.text("Date:"+usds.storage[Name_date]['date']())
    st.text("Time:"+usds.storage[Name_date]['time']())
    st.text("Associate Name:"+usds.storage[Name_date]['Aname']())
    st.text("PhoneNumber: "+str(usds.storage[Name_date]['APhoneNumber']()))
    st.text("Mineral Name: "+str(usds.storage[Name_date]['Mname']()))
    st.text("Place of origin : "+usds.storage[Name_date]['loo']())
    st.text("Quantity: "+usds.storage[Name_date]['qty']())
    st.text("Quality Status: "+usds.storage[Name_date]['qts']())
    st.text("Quality points: "+usds.storage[Name_date]['Quality']())
    st.text("Comments: "+usds.storage[Name_date]['comment']())


   
